export const initialState={
  isLoading:true,
  error:"",
  post:{},
};

const reducer = (state,action)=>{
  switch (action.type) {
    case "FETCH_SUC":
      return {...state,post:action.payload}
    case "FETCH_ERR":
      return {isLoading:false,error}
    default:
      return state
    
  }
}

export default reducer
