export const initialState={
  post:0,
  secondCount:2
};

const reducer = (state,action)=>{
  switch (action) {
    case "increment":
      return {...state,firstCount:state.firstCount + 1,secondCount:state.secondCount+10}
    case "decrement":
      return {...state,firstCount:state.firstCount - 1}
    case "reset":
      return initialState
    default:
      return state;
  }
}

export default reducer
