export const initialState={
  isLoading:true,
  error:"",
  users:[],
};

const reducer = (state,action)=>{
  switch (action.type) {
    case "FETCH_SUC":
      return {isLoading:false,error:"",post:action.payload}
    case "FETCH_ERR":
      return {isLoading:false,error:action.payload,post:{}}
    default:
      return state
    
  }
}

export default reducer
